import React, { useState } from "react";
import "./FormExpense.css";

const FormExpense = (props) => {
  const [inputName, setInputName] = useState("");
  const [inputPrice, setInputPrice] = useState("");
  const [inputDate, setInputDate] = useState("");

  const [visibilityForm, setVisibilityForm] = useState(false);
  // const [userInputs, setUserInputs] = useState({
  //   inputName: "",
  //   inputPrice: "",
  //   inputDate: "",
  // });

  const changeName = (e) => {
    setInputName(e.target.value);

    // setUserInputs({
    //   ...userInputs,
    //   inputName: e.target.value,
    // });

    // setUserInputs((prevValue)=>{
    //   //questo metodo è utile se ci basiamo su dei valori precedenti (come i counter)
    //   return ({...prevValue, inputName: e.target.value})
    // })
  };
  const changePrice = (e) => {
    setInputPrice(e.target.value);
    // setUserInputs({
    //   ...userInputs,
    //   inputPrice: e.target.value,
    // });
  };
  const changeDate = (e) => {
    setInputDate(e.target.value);
    // setUserInputs({
    //   ...userInputs,
    //   inputDate: e.target.value,
    // });
  };

  const saveDataForm = (e) => {
    e.preventDefault();

    const dataForm = {
      id: Math.random(),
      name: inputName,
      price: inputPrice,
      date: new Date(inputDate),
    };

    props.saveDataFormExpense(dataForm);

    setInputName("");
    setInputPrice("");
    setInputDate("");
    console.log(visibilityForm);
    setVisibilityForm(true);
    console.log(visibilityForm);
  };

  return (
    <form className="form_expense" onSubmit={saveDataForm}>
      <div className="row">
        <div className="col-3">
          <div>
            <label>Spesa</label>
          </div>
          <div>
            <input
              type="text"
              name="expense_name"
              value={inputName}
              onChange={changeName}
            />
          </div>
        </div>
        <div className="col-3">
          <div>
            <label>Prezzo</label>
          </div>
          <div>
            <input
              type="number"
              name="expense_price"
              min="0.1"
              step="0.1"
              value={inputPrice}
              onChange={changePrice}
            />
          </div>
        </div>
        <div className="col-3">
          <div>
            <label>Data</label>
          </div>
          <div>
            <input
              type="date"
              name="expense_date"
              min="2019-01-01"
              max="2022-12-31"
              value={inputDate}
              onChange={changeDate}
            />
          </div>
        </div>
        <div className="col-3 input_container">
          <button type="submit">Aggiungi spesa</button>
        </div>
      </div>
    </form>
  );
};

export default FormExpense;
