import React, { useState, useRef } from "react";
import { ExpenseItem } from "./components/ExpenseItem";
import { Card } from "./components/Card.js";

function App() {
  const [itemVis, setItemVis] = useState(false);
  const data = useRef(null);
  const thing = useRef(null);
  const price = useRef(null);

  const itemsInit = [
    {
      data: new Date(Date.UTC(2021, 5, 21)),
      thing: "Bolletta Vodafone",
      price: 85.0,
    },
    {
      data: new Date(Date.UTC(2021, 6, 21)),
      thing: "Pizza",
      price: 18.5,
    },
    {
      data: new Date(Date.UTC(2021, 11, 20)),
      thing: "Benzina",
      price: 50.0,
    },
  ];
  const [items, setItems] = useState(itemsInit);

  //rendo visibile il form
  const addItem = () => {
    setItemVis(true);
    data.current.value = null;
    thing.current.value = null;
    price.current.value = null;
  };

  //creo il nuovo task
  const sendData = (e) => {
    e.preventDefault();
    console.log(parseInt(price.current.value));

    if (
      data.current.value === "" ||
      thing.current.value === "" ||
      price.current.value === ""
    ) {
      alert("Inserire valori corretti");
    } else {
      console.log(
        data.current.value.slice(0, 4),
        data.current.value.slice(5, 7),
        data.current.value.slice(8, 10)
      );
      const itemTmp = {
        data: new Date(
          Date.UTC(
            data.current.value.slice(0, 4),
            data.current.value.slice(5, 7),
            data.current.value.slice(8, 10)
          )
        ),
        thing: thing.current.value.toString(),
        price: parseFloat(price.current.value),
      };
      setItems([...items, itemTmp]);
      setItemVis(false);
    }
  };

  //calcolo la somma totale
  const priceArr = [];
  items.forEach((el) => priceArr.push(el.price));
  var sum = 0;
  for (var i = 0; i < priceArr.length; i++) {
    sum += priceArr[i];
  }

  return (
    <div className="App">
      <h1 className="big_title">Calcola le spese giornaliere</h1>

      <button onClick={addItem}>Aggiungi spesa</button>
      {/* alt + \ per il backthic */}
      <form
        className="input_container"
        style={{ display: `${itemVis ? "block" : "none"}` }}
      >
        <label>Data</label>
        <input type="date" ref={data} />

        <label>Spesa</label>
        <input type="text" ref={thing} />

        <label>Prezzo</label>
        <input type="number" ref={price} />

        <button type="submit" onClick={sendData}>
          Conferma
        </button>
      </form>

      <Card className='my_card'>
        {items.map((el) => {
          return <ExpenseItem key={el.thing} item={el} />;
        })}
      </Card>

      <div className="total">
        Totale: <span>{sum}</span>
      </div>
    </div>
  );
}

export default App;
