import React, { useState } from "react";
import { ExpenseItem } from "./ExpenseItem";
import ExpenseFilter from "./ExpenseFilter";

export function Expenses({ items }) {
  const [yearSelected, setYearSelected] = useState("all");

  const saveDataExpenseFilter = (data) => {
    const dataFilter = data;

    setYearSelected(dataFilter);
    console.log();
  };

  const filteredItems = items.filter((el) => {
    return (
      el.date.toLocaleString("it-IT", { year: "numeric" }) === yearSelected
    );
  });

  return (
    <>
      <ExpenseFilter
        saveDataExpenseFilter={saveDataExpenseFilter}
        yearSelected={yearSelected}
      />
      {yearSelected === "all"
        ? items.map((el) => {
            return <ExpenseItem key={el.id} item={el} />;
          })
        : items.filter((el) => {
            // console.log('item:'+el.date.toLocaleString("it-IT", { year: "numeric" }))
            // console.log('select:'+yearSelected)
            //return ( value.date.toLocaleString("it-IT", { year: "numeric" }) === yearSelected );
            return "ciao";
          })}
    </>
  );
}
