const ChartBar = (props)=> {

    let chartHeight = '0%';

    if (props.value>0) {
        chartHeight = Math.round( (props.value/props.maxVal) * 100) + '%';
    }

    return <div className="chart_bar">
        <div className='chart_content'></div>
        <div className='chart_lable'>{props.label}</div>
    </div>
}

export default ChartBar;
