import React from "react";
import { Expenses } from "./components/Expenses/Expenses";
import { Card } from "./components/UI/Card";
import NewExpense from "./components/NewExpense/NewExpense";

function App() {

  const itemsInit = [
    {
      data: new Date(Date.UTC(2021, 5, 21)),
      thing: "Bolletta Vodafone",
      price: 85.0,
    },
    {
      data: new Date(Date.UTC(2021, 6, 21)),
      thing: "Pizza",
      price: 18.5,
    },
    {
      data: new Date(Date.UTC(2021, 11, 20)),
      thing: "Benzina",
      price: 50.0,
    },
  ];

  const saveDataExpense = (data)=> {
    const dataExpense = {
      ...data,
    }
    console.log(dataExpense);
  }

  return (
    <div className="App">
      <h1 className="big_title">Calcola le spese giornaliere</h1>

      <Card className='expenses'>
        <NewExpense saveDataExpense={saveDataExpense}/>
        <Expenses items={itemsInit}/>
      </Card>
    </div>
  );
}

export default App;
