import React, {useState} from "react";
import { Expenses } from "./components/Expenses/Expenses";
import { Card } from "./components/UI/Card";
import NewExpense from "./components/NewExpense/NewExpense";

function App() {

  const itemsInit = [
    {
      date: new Date(Date.UTC(2021, 5, 21)),
      name: "Bolletta Vodafone",
      price: 85.0,
    },
    {
      date: new Date(Date.UTC(2021, 6, 21)),
      name: "Pizza",
      price: 18.5,
    },
    {
      date: new Date(Date.UTC(2021, 11, 20)),
      name: "Benzina",
      price: 50.0,
    },
  ];
  const [items, setItems] = useState(itemsInit);

  const saveDataExpense = (data)=> {
    // console.log(data.date);
    setItems( (prev)=>{
      //console.log(prev)
      return [data, ...prev]
    })
  }

  return (
    <div className="App">
      <h1 className="big_title">Calcola le spese giornaliere</h1>

      <Card className='expenses'>
        <NewExpense saveDataExpense={saveDataExpense}/>
        {/* <Expenses items={items}/> */}
      </Card>
    </div>
  );
}

export default App;
