import React, { useState } from "react";
import "./FormExpense.css";

const FormExpense = () => {
  const [inputName, setInputName] = useState('');
  const [inputPrice, setInputPrice] = useState(0);
  const [inputDate, setInputDate] = useState('');
  // const [userInputs, setUserInputs] = useState({
  //   inputName: "",
  //   inputPrice: "",
  //   inputDate: "",
  // });

  const changeName = (e) => {
    setInputName(e.target.value);

    // setUserInputs({
    //   ...userInputs,
    //   inputName: e.target.value,
    // });

    // setUserInputs((prevValue)=>{
    //   //questo metodo è utile se ci basiamo su dei valori precedenti (come i counter)
    //   return ({...prevValue, inputName: e.target.value})
    // })
  };
  const changePrice = (e) => {
    setInputPrice(e.target.value)
    // setUserInputs({
    //   ...userInputs,
    //   inputPrice: e.target.value,
    // });
  };
  const changeDate = (e) => {
    setInputDate(e.target.value);
    // setUserInputs({
    //   ...userInputs,
    //   inputDate: e.target.value,
    // });
  };

  const saveDataForm = ()=> {
    const dataForm = {
      name: inputName,
      price: inputPrice,
      date: new Date(inputDate),
    }

    console.log(dataForm);

    setInputName('');
    setInputPrice('');
    setInputDate('');
  }

  return (
    <form className="form_expense" onSubmit={saveDataForm}>
      <div className="row">
        <div className="col-3">
          <label>Spesa</label>
          <input type="text" name="expense_name" onChange={changeName} />
        </div>
        <div className="col-3">
          <label>Prezzo</label>
          <input
            type="number"
            name="expense_price"
            min="0.1"
            step="0.1"
            onChange={changePrice}
          />
        </div>
        <div className="col-3">
          <label>Data</label>
          <input
            type="date"
            name="expense_date"
            min="2019-01-01"
            max="2022-12-31"
            onChange={changeDate}
          />
        </div>
        <div className="col-3 input_container">
          <button type="submit">Aggiungi spesa</button>
        </div>
      </div>
    </form>
  );
};

export default FormExpense;
