import React, { useState } from "react";
import { ExpenseItem } from "./ExpenseItem";
import ExpenseFilter from "./ExpenseFilter";

export function Expenses({ items }) {

  const itemsInit = [
    {
      data: new Date(Date.UTC(2021, 5, 21)),
      thing: "Bolletta Vodafone",
      price: 85.0,
    },
    {
      data: new Date(Date.UTC(2021, 6, 21)),
      thing: "Pizza",
      price: 18.5,
    },
    {
      data: new Date(Date.UTC(2021, 11, 20)),
      thing: "Benzina",
      price: 50.0,
    },
  ];

  const [initYear, setInitYear] = useState("");

  const saveDataExpenseFilter = (data) => {
    const dataFilter = data;
    console.log("Sono nell'expenses");
    console.log(dataFilter);
    setInitYear(dataFilter);
  };

  return (
    <>
      <ExpenseFilter
        saveDataExpenseFilter={saveDataExpenseFilter}
        initYear={initYear}
      />
      {items.map((el) => {
        return <ExpenseItem item={el} />;
      })}
    </>
  );
}
