        console.log(item.date.getMonth())
    }); 

   for (let item in dataChart) {
       const monthChart = item.date.getMonth();
       dataChart[monthChart].value += item.price
   }

   console.log(dataChart);

    return <Chart/>
}

export default ExpenseChart;
