import "./ExpenseItem.css";

function ExpenseItem({ item }) {
  //console.log(item);
  const { data, thing, price } = item;

  const monthFormat = data.toLocaleString("it-IT", { month: "long" });
  const dayFormat = data.toLocaleString("it-IT", { day: "2-digit" });
  const yearFormat = data.toLocaleString("it-IT", { year: "numeric" });

  return (
    <div className="container">
      <div className="expense_item">
        <div className="row">
          <div className="expense_data col-4">
            <div>{monthFormat}</div>
            <div>{dayFormat}</div>
            <div>{yearFormat}</div>
          </div>
          <div className="expense_thing col-4">{thing}</div>
          <div className=" col-4 expense_price_container">
            <span className="expense_price">â‚¬{price}</span>
          </div>
        </div>
      </div>
    </div>
  );
}

export { ExpenseItem };
